        /// <summary>
        /// 把汉字转换成拼音(全拼)
        /// </summary>
        /// <param name="hzString">汉字字符串</param>
        /// <param name="OnlyAleph">是否只输出首字母</param>
        /// <param name="AllUpper">是否全部大写</param>
        /// <param name="UseSpace">是否使用空格间隔每个字符</param>
        /// <returns>转换后的拼音(全拼)字符串</returns>
        public static string Convert(string hzString, bool OnlyAleph = false, bool AllUpper = false, bool UseSpace = false)
        {
            // 匹配中文字符
            Regex regex = new Regex("^[\u4e00-\u9fa5]$");
            byte[] array = new byte[2];
            string pyString = "";
            int chrAsc = 0;
            int i1 = 0;
            int i2 = 0;
            char[] noWChar = hzString.ToCharArray();

            for (int j = 0; j < noWChar.Length; j++)
            {
                // 中文字符
                if (regex.IsMatch(noWChar[j].ToString()))
                {
                    array = Encoding.Default.GetBytes(noWChar[j].ToString());
                    i1 = array[0];
                    i2 = array[1];
                    chrAsc = i1 * 256 + i2 - 65536;
                    if (chrAsc > 0 && chrAsc < 160)
                    {
                        pyString += noWChar[j];
                    }
                    else
                    {
                        // 修正部分文字
                        if (chrAsc == -9254) // 修正“圳”字
                            pyString += OnlyAleph ? "Z" : "Zhen";
                        else
                        {
                            for (int i = (pyValue.Length - 1); i >= 0; i--)
                            {
                                if (pyValue[i] <= chrAsc)
                                {
                                    pyString += OnlyAleph ? pyName[i].Substring(0, 1) : pyName[i];
                                    if (UseSpace)
                                    {
                                        pyString += " ";
                                    }
                                    break;
                                }
                            }
                        }
                    }
                }
                // 非中文字符
                else
                {
                    pyString += noWChar[j].ToString();
                }
            }
            if (AllUpper)
            {
                pyString = pyString.ToUpper();
            }
            return pyString;
        }
